#include <mach/mach_time.h>class flixel {public:	static int systemMillisecs() {		mach_timebase_info_data_t timeInfo;		mach_timebase_info(&timeInfo);				uint64_t nanos = mach_absolute_time();				nanos *= timeInfo.numer;		nanos /= timeInfo.denom;		return nanos/1000000L;	}		static void showMouse() {	}		static void hideMouse() {	}		static bool isMobile() {		return true;	}		static void openURL(String url) {		NSString *NSstrURL = address.ToNSString();		[[UIApplication sharedApplication] openURL:[NSURL URLWithString:NSstrURL]];	}	};